import React, { useState, useEffect } from 'react';
import dataService from '../services/dataService';
import toastService from '../services/toastService';
import Modal from './Modal';
import Spinner from './Spinner';

const IngredientsPage = () => {
  // L·∫•y d·ªØ li·ªáu t·ª´ service
  const [ingredients, setIngredients] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingIngredient, setEditingIngredient] = useState(null);
  const [formData, setFormData] = useState({ name: '', unit: '' });
  const [isDataLoading, setIsDataLoading] = useState(true);

  // Load d·ªØ li·ªáu khi component mount
  useEffect(() => {
    const loadIngredients = async () => {
      setIsDataLoading(true);
      try {
        // ƒê·∫£m b·∫£o dataService ƒë√£ ƒë∆∞·ª£c kh·ªüi t·∫°o
        if (!dataService.isInitialized) {
          await dataService.init();
        }
        setIngredients(dataService.getIngredients());
        console.log('Loaded ingredients:', dataService.getIngredients().length);
      } catch (error) {
        console.error('Error loading ingredients:', error);
        setIngredients([]);
      } finally {
        setIsDataLoading(false);
      }
    };
    
    loadIngredients();
  }, []);

  // H√†m m·ªü modal th√™m m·ªõi
  const openAddModal = () => {
    setEditingIngredient(null);
    setFormData({ name: '', unit: '' });
    setIsModalOpen(true);
  };

  // H√†m m·ªü modal ch·ªânh s·ª≠a
  const openEditModal = (ingredient) => {
    setEditingIngredient(ingredient);
    setFormData({ name: ingredient.name, unit: ingredient.unit });
    setIsModalOpen(true);
  };

  // H√†m ƒë√≥ng modal
  const closeModal = () => {
    setIsModalOpen(false);
    setEditingIngredient(null);
    setFormData({ name: '', unit: '' });
  };

  // H√†m x·ª≠ l√Ω thay ƒë·ªïi form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // H√†m l∆∞u nguy√™n li·ªáu
  const handleSave = () => {
    if (!formData.name.trim() || !formData.unit.trim()) {
      toastService.warning('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin!');
      return;
    }

    if (editingIngredient) {
      // C·∫≠p nh·∫≠t nguy√™n li·ªáu
      dataService.updateIngredient(editingIngredient.id, formData);
      setIngredients(dataService.getIngredients());
      toastService.success('ƒê√£ c·∫≠p nh·∫≠t nguy√™n li·ªáu th√†nh c√¥ng!');
    } else {
      // Th√™m nguy√™n li·ªáu m·ªõi
      dataService.addIngredient(formData);
      setIngredients(dataService.getIngredients());
      toastService.success('ƒê√£ th√™m nguy√™n li·ªáu m·ªõi th√†nh c√¥ng!');
    }
    
    closeModal();
  };

  // H√†m x√≥a nguy√™n li·ªáu
  const handleDelete = async (ingredientId) => {
    if (window.confirm('B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a nguy√™n li·ªáu n√†y? H√†nh ƒë·ªông n√†y s·∫Ω x√≥a nguy√™n li·ªáu kh·ªèi t·∫•t c·∫£ c√¥ng th·ª©c.')) {
      try {
        await dataService.deleteIngredient(ingredientId);
        setIngredients(dataService.getIngredients());
        toastService.success('ƒê√£ x√≥a nguy√™n li·ªáu th√†nh c√¥ng!');
      } catch (error) {
        console.error('L·ªói khi x√≥a nguy√™n li·ªáu:', error);
        toastService.error('C√≥ l·ªói x·∫£y ra khi x√≥a nguy√™n li·ªáu!');
      }
    }
  };

  // Show spinner while loading data
  if (isDataLoading) {
    return (
      <div className="container" style={{ paddingTop: '2rem', paddingBottom: '2rem' }}>
        <Spinner text="ƒêang t·∫£i danh s√°ch nguy√™n li·ªáu..." />
      </div>
    );
  }

  return (
    <div className="container" style={{ paddingTop: '2rem', paddingBottom: '2rem' }}>
      {/* Header */}
      <div className="card text-center fade-in" style={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        border: 'none',
        marginBottom: '2rem'
      }}>
        <h1 className="text-xl" style={{ 
          marginBottom: '0.5rem',
          fontSize: '2.5rem',
          fontWeight: 'bold'
        }}>
          ü•§ Qu·∫£n L√Ω Nguy√™n Li·ªáu
        </h1>
        <p className="text-lg" style={{ opacity: 0.9 }}>
          Th√™m, s·ª≠a, x√≥a nguy√™n li·ªáu v√† ƒë∆°n v·ªã ƒëo
        </p>
      </div>

      {/* Th·ªëng k√™ */}
      <div className="card fade-in" style={{ marginBottom: '2rem' }}>
        <div className="grid grid-4 text-center">
          <div>
            <div className="text-lg font-bold text-primary">{ingredients.length}</div>
            <div className="text-sm text-muted">T·ªïng nguy√™n li·ªáu</div>
          </div>
          <div>
            <div className="text-lg font-bold text-success">
              {[...new Set(ingredients.map(ing => ing.unit))].length}
            </div>
            <div className="text-sm text-muted">ƒê∆°n v·ªã ƒëo</div>
          </div>
          <div>
            <div className="text-lg font-bold text-warning">
              {ingredients.filter(ing => ing.unit === 'g').length}
            </div>
            <div className="text-sm text-muted">Theo gram</div>
          </div>
          <div>
            <div className="text-lg font-bold text-danger">
              {ingredients.filter(ing => ing.unit === 'ml').length}
            </div>
            <div className="text-sm text-muted">Theo ml</div>
          </div>
        </div>
      </div>

      {/* N√∫t th√™m m·ªõi */}
      <div className="card fade-in" style={{ marginBottom: '2rem' }}>
        <div className="flex-between">
          <h2 className="text-lg font-bold">üìã Danh S√°ch Nguy√™n Li·ªáu</h2>
          <button
            onClick={openAddModal}
            className="btn btn-success"
          >
            ‚ûï Th√™m Nguy√™n Li·ªáu
          </button>
        </div>
      </div>

      {/* Danh s√°ch nguy√™n li·ªáu */}
      <div className="card fade-in">
        {ingredients.length > 0 ? (
          <div className="grid grid-3" style={{ gap: '1rem' }}>
            {ingredients.map(ingredient => (
              <div key={ingredient.id} className="card" style={{
                padding: '1rem',
                backgroundColor: '#f8f9fa',
                border: '2px solid #e9ecef',
                transition: 'all 0.3s ease'
              }}>
                <div className="text-center mb-2">
                  <div className="font-bold text-lg" style={{ color: '#333' }}>
                    {ingredient.name}
                  </div>
                  <div className="badge badge-primary">
                    {ingredient.unit}
                  </div>
                </div>
                
                <div className="flex gap-1 justify-center">
                  <button
                    onClick={() => openEditModal(ingredient)}
                    className="btn btn-warning btn-sm"
                    style={{ flex: 1 }}
                  >
                    ‚úèÔ∏è S·ª≠a
                  </button>
                  <button
                    onClick={() => handleDelete(ingredient.id)}
                    className="btn btn-danger btn-sm"
                    style={{ flex: 1 }}
                  >
                    üóëÔ∏è X√≥a
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center" style={{ padding: '2rem' }}>
            <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>ü•§</div>
            <p className="text-muted text-lg">
              Ch∆∞a c√≥ nguy√™n li·ªáu n√†o
            </p>
            <button
              onClick={openAddModal}
              className="btn btn-primary mt-2"
            >
              ‚ûï Th√™m Nguy√™n Li·ªáu ƒê·∫ßu Ti√™n
            </button>
          </div>
        )}
      </div>

      {/* Modal th√™m/s·ª≠a nguy√™n li·ªáu */}
      <Modal
        isOpen={isModalOpen}
        onClose={closeModal}
        title={editingIngredient ? `‚úèÔ∏è S·ª≠a Nguy√™n Li·ªáu` : `‚ûï Th√™m Nguy√™n Li·ªáu M·ªõi`}
        size="small"
      >
        <div className="fade-in">
          <div className="form-group mb-3">
            <label className="form-label">T√™n nguy√™n li·ªáu</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              className="form-control"
              placeholder="V√≠ d·ª•: C√† ph√™, S·ªØa t∆∞∆°i, ƒê∆∞·ªùng..."
            />
          </div>

          <div className="form-group mb-3">
            <label className="form-label">ƒê∆°n v·ªã ƒëo</label>
            <select
              name="unit"
              value={formData.unit}
              onChange={handleInputChange}
              className="form-control"
            >
              <option value="">Ch·ªçn ƒë∆°n v·ªã ƒëo</option>
              <option value="g">Gram (g)</option>
              <option value="ml">Milliliter (ml)</option>
              <option value="qu·∫£">Qu·∫£</option>
              <option value="ly">Ly</option>
              <option value="mu·ªóng">Mu·ªóng</option>
              <option value="g√≥i">G√≥i</option>
              <option value="kg">Kilogram (kg)</option>
              <option value="l">Liter (l)</option>
              <option value="c√°i">C√°i</option>
              <option value="mi·∫øng">Mi·∫øng</option>
            </select>
          </div>

          <div className="flex gap-2 justify-center" style={{ 
            paddingTop: '0.5rem',
            borderTop: '1px solid #e5e7eb'
          }}>
            <button
              onClick={handleSave}
              className="btn btn-success btn-sm"
              style={{ minWidth: '80px' }}
            >
              üíæ L∆∞u
            </button>
            <button
              onClick={closeModal}
              className="btn btn-secondary btn-sm"
              style={{ minWidth: '80px' }}
            >
              ‚ùå H·ªßy
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default IngredientsPage;
